/**
 *  Init datatables
 *
 */
function initDatatables() {
    var $dataTable = $("#faculties-datatables");
    if ($.fn.DataTable.isDataTable($dataTable)) {
        $dataTable.DataTable().ajax.reload(null, false);
    } else {
        $dataTable.DataTable({
            responsive: true,
            processing: true,
            serverSide: true,
            order: [0, "desc"],
            ajax: $("#faculties-datatables").attr("data-source"),
            createdRow: function (row, data, dataIndex, cells) {
                $(row).addClass("record-row");
            },
            columns: [
                {
                    data: 'order',
                    name: 'order',
                    className: 'align-middle text-center',
                    width: '10%',
                    title: 'No.',
                },
                {
                    data: 'code',
                    name: 'code',
                    className: 'align-middle',
                    width: '10%',
                    title: 'Kode Fakultas',
                },
                {
                    data: 'name',
                    name: 'name',
                    className: 'align-middle',
                    title: 'Nama Fakultas'
                }
            ]
        });
    }
}

$(document).ready(function () {

    initDatatables();

});
