/**
 *  Init datatables
 *
 */
function initDatatables() {
    var $dataTable = $("#study-programs-datatables");
    if ($.fn.DataTable.isDataTable($dataTable)) {
        $dataTable.DataTable().ajax.reload(null, false);
    } else {
        $dataTable.DataTable({
            responsive: true,
            processing: true,
            serverSide: true,
            order: [0, "desc"],
            ajax: $("#study-programs-datatables").attr("data-source"),
            createdRow: function(row, data, dataIndex, cells) {
                $(row).addClass("record-row");
            },
            columns: [
                {
                    data: 'code',
                    name: 'study_programs.code',
                    className: 'align-middle text-center',
                    width: '10%',
                    title: 'Kode',
                },
                {
                    data: 'alias',
                    name: 'study_programs.alias',
                    className: 'align-middle',
                    width: '10%',
                    title: 'Alias',
                },
                {
                    data: 'faculty',
                    name: 'faculty.name',
                    width: '20%',
                    className: 'align-middle',
                    title: 'Nama Fakultas'
                },
                {
                    data: 'name',
                    name: 'study_programs.name',
                    className: 'align-middle',
                    title: 'Nama Program Studi',
                }
            ]
        });
    }
}

$(document).ready(function () {

    initDatatables();

});
